import React, { useState } from "react";
import Modal from "./components/Modal";
import Spinner from "./components/Spinner";
import Toast from "./components/Toast";

function App() {
  const [modal, setModal] = useState(null); // 'signin' | 'signup' | null
  // Sign In state
  const [signInEmail, setSignInEmail] = useState("");
  const [signInPassword, setSignInPassword] = useState("");
  // Sign Up state
  const [signUpFirst, setSignUpFirst] = useState("");
  const [signUpLast, setSignUpLast] = useState("");
  const [signUpEmail, setSignUpEmail] = useState("");
  const [signUpPassword, setSignUpPassword] = useState("");
  // Loading and Toast
  const [loading, setLoading] = useState(false);
  const [toast, setToast] = useState({ message: "", type: "success", visible: false });

  // Backend API base URL
  const API = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

  // Sign In handler
  async function handleSignIn(e) {
    e.preventDefault();
    setLoading(true);
    setToast({ message: "", type: "success", visible: false });
    try {
      const res = await fetch(`${API}/login-user`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: signInEmail, password: signInPassword })
      });
      const data = await res.json();
      if (res.ok) {
        setToast({ message: "Signed in successfully!", type: "success", visible: true });
        setTimeout(() => setModal(null), 1000);
      } else {
        setToast({ message: data.error || "Sign in failed", type: "error", visible: true });
      }
    } catch (err) {
      setToast({ message: "Network error", type: "error", visible: true });
    }
    setLoading(false);
  }

  // Sign Up handler
  async function handleSignUp(e) {
    e.preventDefault();
    setLoading(true);
    setToast({ message: "", type: "success", visible: false });
    try {
      const res = await fetch(`${API}/register-user`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          firstName: signUpFirst,
          lastName: signUpLast,
          email: signUpEmail,
          password: signUpPassword
        })
      });
      const data = await res.json();
      if (res.ok) {
        setToast({ message: "Account created! Check your email.", type: "success", visible: true });
        setTimeout(() => setModal(null), 1500);
      } else {
        setToast({ message: data.error || (data.errors && data.errors[0]?.msg) || "Sign up failed", type: "error", visible: true });
      }
    } catch (err) {
      setToast({ message: "Network error", type: "error", visible: true });
    }
    setLoading(false);
  }
  return (
      <div className="min-h-screen bg-gray-50 font-sans">
        {/* Navigation */}
        <nav className="bg-white shadow-sm border-b border-gray-100">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center h-16">
              <div className="flex items-center">
                <h1 className="text-2xl font-bold text-blue-900">Neighbiz</h1>
              </div>
              <div className="flex items-center space-x-4">
                <button onClick={() => setModal('signin')} className="text-blue-900 hover:text-teal-600 font-medium">Sign In</button>
                <button onClick={() => setModal('signup')} className="bg-teal-600 text-white px-6 py-2 rounded-full hover:bg-opacity-90 font-medium">Get Started</button>
              </div>
            </div>
          </div>
        </nav>

        {/* Hero Section */}
        <section className="bg-gradient-to-br from-blue-900 to-teal-600 text-white py-20">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
            <h1 className="text-5xl font-bold mb-6">Connect with Your Neighborhood</h1>
            <p className="text-xl mb-8 max-w-2xl mx-auto">Discover local businesses and services right in your area. From home repairs to beauty services, find trusted neighbors ready to help.</p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <button className="bg-white text-blue-900 px-8 py-4 rounded-full font-semibold hover:bg-gray-100 transition-colors">Find Local Services</button>
              <button className="border-2 border-white text-white px-8 py-4 rounded-full font-semibold hover:bg-white hover:text-blue-900 transition-colors">List Your Business</button>
            </div>
          </div>
        </section>

        {/* Features Section */}
        <section className="py-20 bg-white">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center mb-16">
              <h2 className="text-4xl font-bold text-blue-900 mb-4">How Neighbiz Works</h2>
              <p className="text-gray-600 text-lg">Simple, secure, and local</p>
            </div>
            <div className="grid md:grid-cols-3 gap-8">
              <div className="text-center p-6">
                <div className="w-16 h-16 bg-teal-600 rounded-full flex items-center justify-center mx-auto mb-4">
                  <span className="text-white text-2xl">📍</span>
                </div>
                <h3 className="text-xl font-semibold text-blue-900 mb-2">Set Your Radius</h3>
                <p className="text-gray-600">Choose how far you want to search - 5km, 10km, or 15km from your location.</p>
              </div>
              <div className="text-center p-6">
                <div className="w-16 h-16 bg-teal-600 rounded-full flex items-center justify-center mx-auto mb-4">
                  <span className="text-white text-2xl">💬</span>
                </div>
                <h3 className="text-xl font-semibold text-blue-900 mb-2">Chat Directly</h3>
                <p className="text-gray-600">Message businesses directly through our secure in-app chat system.</p>
              </div>
              <div className="text-center p-6">
                <div className="w-16 h-16 bg-teal-600 rounded-full flex items-center justify-center mx-auto mb-4">
                  <span className="text-white text-2xl">📅</span>
                </div>
                <h3 className="text-xl font-semibold text-blue-900 mb-2">Book Appointments</h3>
                <p className="text-gray-600">Schedule services directly with businesses that offer appointment booking.</p>
              </div>
            </div>
          </div>
        </section>

        {/* Footer */}
        <footer className="bg-blue-900 text-white py-12 mt-12">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
            <h3 className="text-xl font-bold mb-4">Neighbiz</h3>
            <p className="text-gray-300">Connecting communities through local business services.</p>
            <div className="border-t border-gray-700 mt-8 pt-8 text-center text-gray-300">
              <p>&copy; 2024 Neighbiz. All rights reserved.</p>
            </div>
          </div>
        </footer>
        {/* Modals */}
        <Modal open={modal === 'signin'} onClose={() => setModal(null)} title="Sign In">
          <form className="space-y-4" onSubmit={handleSignIn}>
            <input
              className="w-full px-4 py-3 border border-gray-300 rounded-xl"
              type="email"
              placeholder="Email"
              value={signInEmail}
              onChange={e => setSignInEmail(e.target.value)}
              required
            />
            <input
              className="w-full px-4 py-3 border border-gray-300 rounded-xl"
              type="password"
              placeholder="Password"
              value={signInPassword}
              onChange={e => setSignInPassword(e.target.value)}
              required
            />
            <button className="w-full bg-teal-600 text-white py-3 rounded-xl font-semibold hover:bg-opacity-90 transition-colors" disabled={loading}>Sign In</button>
            {loading && <Spinner />}
          </form>
        </Modal>
        <Modal open={modal === 'signup'} onClose={() => setModal(null)} title="Sign Up">
          <form className="space-y-4" onSubmit={handleSignUp}>
            <input
              className="w-full px-4 py-3 border border-gray-300 rounded-xl"
              type="text"
              placeholder="First Name"
              value={signUpFirst}
              onChange={e => setSignUpFirst(e.target.value)}
              required
            />
            <input
              className="w-full px-4 py-3 border border-gray-300 rounded-xl"
              type="text"
              placeholder="Last Name"
              value={signUpLast}
              onChange={e => setSignUpLast(e.target.value)}
              required
            />
            <input
              className="w-full px-4 py-3 border border-gray-300 rounded-xl"
              type="email"
              placeholder="Email"
              value={signUpEmail}
              onChange={e => setSignUpEmail(e.target.value)}
              required
            />
            <input
              className="w-full px-4 py-3 border border-gray-300 rounded-xl"
              type="password"
              placeholder="Password"
              value={signUpPassword}
              onChange={e => setSignUpPassword(e.target.value)}
              required
            />
            <button className="w-full bg-teal-600 text-white py-3 rounded-xl font-semibold hover:bg-opacity-90 transition-colors" disabled={loading}>Sign Up</button>
            {loading && <Spinner />}
          </form>
        </Modal>
      {toast.visible && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast({ ...toast, visible: false })}
        />
      )}
      </div>
    );
  }

export default App;
